mapper.select:
SELECT post_uuid, write_id, post_title, post_content, registry_date FROM post_board WHERE 1 = 1
AND post_uuid = #{postUuid}
ORDER BY registry_date DESC LIMIT 1

mapper.update:
UPDATE post_board SET
      board_title = #{postTitle}
    , board_content = #{postContent}
WHERE 1 = 1
  AND board_uuid = #{postUuid}
  AND write_id = #{writeId}

mapper.delete:
DELETE FROM post_board
 WHERE 1 = 1
   AND board_uuid = #{postUuid}
